import React, { Component } from "react";
import "./ElevatorDisplay.css"; // Import CSS file for styling

class ElevatorDisplay extends Component {
  constructor(props) {
    super(props);
    this.state = {
      floor: props.floor,
      direction: props.direction
    };
  }

  render() {
    const { floor, direction } = this.state;
    // Calculate height of elevator based on floor
    const elevatorSize = floor * 70 + "px";
    return (
      <div className="elevator-parent">
        <div className="elevator-container">
          <div className="elevator" style={{ height: 70, marginBottom: elevatorSize}}> Floor {floor} <br /> Direction: {direction}
          </div>
        </div>
      </div>
    );
  }
}
