import React, { useState } from "react";
import ElevatorDisplay from "./ElevatorDisplay";

const App = () => {
  const [floor, setFloor] = useState(0);
  const [direction, setDirection] = useState("UP");
  const [requestedFloor, setRequestedFloor] = useState(0); // New state variable for requested floor

  const handleFloorChange = (event) => {
    const newFloor = parseInt(event.target.value);
    // setFloor(newFloor);
    setRequestedFloor(newFloor);
  };

  const handleRequestElevator = () => {
    // Send request with requestedFloor and direction to the specified URL
    fetch(`http://localhost:8080/elevator/floorButtonPress`, {
      method: "POST",
      body: JSON.stringify({ floor: requestedFloor, button: direction }),
      headers: {
        "Content-Type": "application/json"
      }
    })
      .then(response => response.json())
      .then(data => {
        // Handle response data if needed
        console.log(data);
      })
      .catch(error => {
        // Handle errors if needed
        console.error(error);
      });
  };

  const handleDirectionChange = (event) => {
    const newDirection = event.target.value;
    setDirection(newDirection);
  };


  return (
    <div>
      <h1>Elevator App</h1>
      <form onSubmit={handleRequestElevator}> {/* Add onSubmit event handler to the form */}        <label htmlFor="floor">Floor:</label>
        <input
          type="number"
          id="floor"
          value={floor}
          onChange={handleFloorChange}
          max="19"
          min="0"
        />
        <label htmlFor="direction">Direction:</label>
        <select id="direction" value={direction} onChange={handleDirectionChange}>
          <option value="UP">Up</option>
          <option value="DOWN">Down</option>
        </select>
        <button type="submit">Request Elevator</button>
      </form>
      <div id="elevator-div">
        <ElevatorDisplay floor={floor} direction={direction} />
        <ElevatorDisplay floor={floor} direction={direction} />
        <ElevatorDisplay floor={floor} direction={direction} />
        <ElevatorDisplay floor={floor} direction={direction} />
        <ElevatorDisplay floor={floor} direction={direction} />
        <ElevatorDisplay floor={floor} direction={direction} />
        <ElevatorDisplay floor={floor} direction={direction} />
        <ElevatorDisplay floor={floor} direction={direction} />
        <ElevatorDisplay floor={floor} direction={direction} />
        <ElevatorDisplay floor={floor} direction={direction} />
        <ElevatorDisplay floor={floor} direction={direction} />
        <ElevatorDisplay floor={floor} direction={direction} />
        <ElevatorDisplay floor={floor} direction={direction} />
        <ElevatorDisplay floor={floor} direction={direction} />
        <ElevatorDisplay floor={floor} direction={direction} />
        <ElevatorDisplay floor={floor} direction={direction} />
      </div>
    </div>
  );
};

export default App;
