import React from "react";
import "./ElevatorDisplay.css"; // Import CSS file for styling

const ElevatorDisplay = ({ floor, direction }) => {
  // Calculate height of elevator based on floor
  const elevatorHeight = floor * 20 + "px"; // Assumes each floor is 20px in height
  const elevatorSize = 40 + "px";
  return (
    <div>
      <div className="elevator-container">
        {/* Render the elevator */}
        <div className="elevator" style={{ height: elevatorHeight }}>
          Floor {floor}
          <br />
          Direction: {direction}
        </div>
      </div>
    </div>
  );
};

export default ElevatorDisplay;
